mapper.select:
select * from post where po_co_num = #{co_num}
and (po_title like concat('%', #{search}, '%') or po_me_id like concat('%', #{search}, '%'))
and ${type} like concat('%', #{search}, '%')
order by po_num desc limit #{pageStart}, #{perPageNum}

select count(*) from post where po_co_num = #{co_num}
and (po_title like concat('%', #{search}, '%') or po_me_id like concat('%', #{search}, '%'))
and ${type} like concat('%', #{search}, '%')
